css

@import url("https://fonts.googleapis.com/css?family=Press+Start+2P");

html,
body,
pre,
code,
kbd,
samp {
  font-family: "Press Start 2P";
}

.remark-slide {
  border-color: #000;
  border-style: solid;
  border-width: 4px;
}

.remark-slide-content h1 { font-size: 3em; }
.remark-slide-content h2 { font-size: 2em; }
.remark-slide-content h3 { font-size: 1.6em; }

.remark-slide-content {
    font-size: 14px;
}

.remark-code-line-highlighted { background-color: #373832; }

.remark-code {
    font-size: 12px;
}

pre {
  padding: 1.5rem 2rem;
  border-color: #000;
  border-style: solid;
  border-width: 4px;
}

.hljs-github .hljs {
    display: block;
    overflow-x: auto;
    padding: .5em;
    color: #333;
    background: #fff;
}

blockquote {
    position: relative;
    display: inline-block;
    padding: 1rem 1.5rem;
    margin: 8px;
    margin-bottom: 30px;
    background-color: #fff;
    border-style: solid;
    border-width: 4px;
}

blockquote::before {
    bottom: -14px;
    width: 26px;
    height: 10px;
    background-color: #fff;
    border-right: 4px solid #212529;
    border-left: 4px solid #212529;
    position: absolute;
    content: "";
    box-sizing: border-box;
}

blockquote::after {
    bottom: -18px;
    width: 18px;
    height: 4px;
    margin-right: 8px;
    color: #212529;
    background-color: #fff;
    box-shadow: -4px 0, 4px 0, -4px 4px #fff, 0 4px, -8px 4px, -4px 8px, -8px 8px;
    position: absolute;
    content: "";
    box-sizing: border-box;
}

ul li {
  list-style-type: none;
  position: relative;
}

ul li::before {
  position: absolute;
  top: calc(50% - 8px);
  left: -22px;
  content: "";
  width: 2px;
  height: 2px;
  color: #212529;
  box-shadow: 8px 2px,10px 2px,6px 4px,8px 4px,10px 4px,12px 4px,4px 6px,6px 6px,8px 6px,10px 6px,12px 6px,14px 6px,4px 8px,6px 8px,8px 8px,10px 8px,12px 8px,14px 8px,6px 10px,8px 10px,10px 10px,12px 10px,8px 12px,10px 12px;
}

/* Content classes */

.pull-left {
    float: left;
    width: 47%;
}
.pull-right {
    float: right;
    width: 47%;
}
.pull-right ~ p {
    clear: both;
}

.inverse {
    background: #272822;
    color: #777872;
    text-shadow: 0 0 20px #333;
}
.inverse h1, .inverse h2 {
    color: #f3f3f3;
    line-height: 0.8em;
}

/* Icons */

.nes-icon {
  position: relative;
  display: inline-block;
  width: 16px;
  height: 16px;
  margin-right: 16px;
  margin-bottom: 16px;
  transform: scale(2);
  transform-origin: top left;
}

.nes-icon.is-small {
  margin: 0;
  transform: scale(1);
}

.nes-icon.is-medium {
  margin-right: 32px;
  margin-bottom: 32px;
  transform: scale(3);
}

.nes-icon.is-large {
  margin-right: 48px;
  margin-bottom: 48px;
  transform: scale(4);
}

.nes-icon::before {
  position: absolute;
  top: -1px;
  left: -1px;
  display: block;
  content: "";
  background: transparent;
}

